import './App.css';
import React, { useState, useRef } from 'react';
import Draggable from 'react-draggable';
import ReactDOM from 'react-dom/client';

function App() {
  const [events, setEvents] = useState(['Event 1', 'Event 2', 'Event 3', 'Event 4']);
  // New state for draggable position
  const [draggablePosition, setDraggablePosition] = useState({ x: 0, y: 0 });
  const draggableRef = useRef(null);

  // Snap to the closest timeline spot (assumed y coordinates)
  const handleStop = (e, data) => {
    const snapPoints = [0, 70, 140, 210]; // Adjust based on timeline event positions
    const closest = snapPoints.reduce((prev, curr) =>
      Math.abs(curr - data.y) < Math.abs(prev - data.y) ? curr : prev
    );
    setDraggablePosition({ x: 0, y: closest });
  };

  return (
    <div className="App" style={{ display: 'flex', flexDirection: 'row', height: '100vh' }}>
      <div style={{ flex: 1, display: 'flex', flexDirection: 'column' }}>
        <header style={{ textAlign: 'center', padding: '20px', backgroundColor: '#282c34', color: 'white' }}>
          <h1>Timeline of Events</h1>
        </header>
        <main style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', flexDirection: 'column', flexGrow: 1 }}>
          {events.map((event, index) => (
            <div key={index} style={{ width: '200px', padding: '10px', margin: '10px 0', backgroundColor: '#f0f0f0', border: '1px solid #ccc', borderRadius: '5px', textAlign: 'center' }}>
              {event}
            </div>
          ))}
        </main>
      </div>
      <div style={{ width: '200px', padding: '20px', backgroundColor: '#f9f9f9', borderLeft: '1px solid #ccc' }}>
        <h2 style={{ textAlign: 'center' }}>Draggable Event</h2>
        <Draggable
          nodeRef={draggableRef}
          position={draggablePosition}
          onStop={handleStop}
        >
          <div ref={draggableRef} style={{ width: '150px', padding: '10px', backgroundColor: '#d1e7dd', border: '1px solid #0f5132', borderRadius: '5px', textAlign: 'center', cursor: 'move' }}>
            Drag Me
          </div>
        </Draggable>
      </div>
    </div>
  );
}

export default App;
