import './App.css';
import React, { useState, useRef } from 'react';
import Draggable from 'react-draggable';
import ReactDOM from 'react-dom/client';

function App() {
  // Change events to objects with a label and y position
  const [events, setEvents] = useState([
    { label: 'Event 1', y: 0 },
    { label: 'Event 2', y: 70 },
    { label: 'Event 3', y: 140 },
    { label: 'Event 4', y: 210 }
  ]);
  const [draggablePosition, setDraggablePosition] = useState({ x: 0, y: 0 });
  const draggableRef = useRef(null);

  // When drop occurs, snap the draggable event and insert a new event in sorted order
  const handleStop = (e, data) => {
    const snapPoints = [0, 70, 140, 210]; // Adjust if needed
    const closest = snapPoints.reduce((prev, curr) =>
      Math.abs(curr - data.y) < Math.abs(prev - data.y) ? curr : prev
    );
    const newEvent = { label: 'New Event', y: closest };
    setEvents(prev => {
      const newEvents = [...prev, newEvent].sort((a, b) => a.y - b.y);
      return newEvents;
    });
    // Reset draggable element position
    setDraggablePosition({ x: 0, y: 0 });
  };

  return (
    <div className="App" style={{ display: 'flex', flexDirection: 'row', height: '100vh' }}>
      <div style={{ flex: 1, display: 'flex', flexDirection: 'column' }}>
        <header style={{ textAlign: 'center', padding: '20px', backgroundColor: '#282c34', color: 'white' }}>
          <h1>Timeline of Events</h1>
        </header>
        {/* Set timeline area to relative for absolute event positioning */}
        <main style={{ position: 'relative', height: '100%', overflow: 'hidden' }}>
          {events.map((event, index) => (
            <div key={index} style={{
                position: 'absolute',
                top: event.y,
                left: 'calc(50% - 100px)',
                width: '200px',
                padding: '10px',
                margin: '10px 0',
                backgroundColor: '#f0f0f0',
                border: '1px solid #ccc',
                borderRadius: '5px',
                textAlign: 'center'
              }}>
              {event.label}
            </div>
          ))}
        </main>
      </div>
      <div style={{ width: '200px', padding: '20px', backgroundColor: '#f9f9f9', borderLeft: '1px solid #ccc' }}>
        <h2 style={{ textAlign: 'center' }}>Draggable Event</h2>
        <Draggable
          nodeRef={draggableRef}
          position={draggablePosition}
          onStop={handleStop}
        >
          <div ref={draggableRef} style={{
              width: '150px',
              padding: '10px',
              backgroundColor: '#d1e7dd',
              border: '1px solid #0f5132',
              borderRadius: '5px',
              textAlign: 'center',
              cursor: 'move'
            }}>
            Drag Me
          </div>
        </Draggable>
      </div>
    </div>
  );
}

export default App;
