//import logo from './logo.svg';
import './App.css';
import React from 'react'

function App() {
  const [events, setEvents] = React.useState(["Event 1", "Event 2", "Event 3", "Event 4"]);

  function onDragStart(e) {
    // Set the dragged data to be the new event text.
    e.dataTransfer.setData("text/plain", "New Event");
  }

  function onDragOver(e) {
    e.preventDefault();
  }

  function onDrop(e, index) {
    e.preventDefault();
    const newEvent = e.dataTransfer.getData("text/plain");
    // Insert the new event into the events list at the drop index.
    let newEvents = [...events];
    newEvents.splice(index, 0, newEvent);
    setEvents(newEvents);
  }

  return (
    <div className="App" style={{ display: 'flex', flexDirection: 'column', height: '100vh' }}>
      <header style={{ textAlign: 'center', padding: '20px', backgroundColor: '#282c34', color: 'white' }}>
        <h1>Timeline of Events</h1>
      </header>
      <main style={{ display: 'flex', flexGrow: 1 }}>
        {/* Timeline events with drop zones */}
        <div style={{ flex: 1, display: 'flex', flexDirection: 'column', alignItems: 'center', padding: '20px' }}>
          {events.map((event, index) => (
            <div key={index} style={{ marginBottom: '10px', width: '100%' }}>
              {/* Drop zone before each event */}
              <div 
                onDragOver={onDragOver} 
                onDrop={(e) => onDrop(e, index)} 
                style={{ width: '200px', height: '20px' }}>
              </div>
              <div style={{ width: '200px', padding: '10px', backgroundColor: '#f0f0f0', border: '1px solid #ccc', borderRadius: '5px', textAlign: 'center' }}>
                {event}
              </div>
            </div>
          ))}
          {/* Drop zone after the last event */}
          <div 
            onDragOver={onDragOver} 
            onDrop={(e) => onDrop(e, events.length)} 
            style={{ width: '200px', height: '20px', marginTop: '10px' }}>
          </div>
        </div>
        {/* Draggable event on the right */}
        <div style={{ width: '250px', borderLeft: '1px solid #ccc', paddingLeft: '20px', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
          <div 
            draggable="true"
            onDragStart={onDragStart}
            style={{ width: '200px', padding: '10px', backgroundColor: '#ffecb3', border: '1px solid #ccc', borderRadius: '5px', textAlign: 'center', cursor: 'grab' }}>
            New Event
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
