//import logo from './logo.svg';
import './App.css';
import React from 'react'
import Draggable from 'react-draggable';

function App() {
  function App() {
    const handleStop = (e, data) => {
      const snapY = Math.round(data.y / 50) * 50; // Snap to 50px increments
      const snapX = Math.round(data.x / 50) * 50; // Snap to 50px increments
      e.target.style.transform = `translate(${snapX}px, ${snapY}px)`;
    };

    return (
      <div className="App" style={{ display: 'flex', flexDirection: 'column', height: '100vh' }}>
        <header style={{ textAlign: 'center', padding: '20px', backgroundColor: '#282c34', color: 'white' }}>
          <h1>Timeline of Events</h1>
        </header>
        <main style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', flexDirection: 'column', flexGrow: 1 }}>
          <div style={{ position: 'relative', width: '100%', height: '400px', border: '1px solid #ccc', backgroundColor: '#eaeaea' }}>
            <Draggable onStop={handleStop}>
              <div style={{ width: '200px', padding: '10px', backgroundColor: '#f0f0f0', border: '1px solid #ccc', borderRadius: '5px', textAlign: 'center', cursor: 'move', position: 'absolute' }}>
                Draggable Event
              </div>
            </Draggable>
          </div>
        </main>
      </div>
    );
  }
}

export default App;
