import './App.css';
import React, { useState, useRef } from 'react';
import Draggable from 'react-draggable';
import ReactDOM from 'react-dom/client';

function App() {
  // Change events to objects with a label and y position
  const [fixedEvents, setFixedEvents] = useState([
    { label: 'Event 1', index: -1},
    { label: 'Event 2', index: 0 },
    { label: 'Event 3', index: 1 },
  ]);


  const mapIndexToY = (index) => {
    const baseY = 0; // Base Y position for the first event
    return baseY + index * 70; // Adjust spacing as needed
  }

  const mapYToIndex = (y) => {
    const baseY = 0; // Base Y position for the first event
    const index = Math.round((y - baseY) / 70); // Adjust spacing as needed
    return index;
  }

  const [draggablePosition, setDraggablePosition] = useState({ x: 0, y: 0 });
  const draggableRef = useRef(null);

  // calculate the index of the draggable event based on its y position
  const getFullTimelineIncludingProspectiveEvent = (draggablePosition) => {
    const index = mapYToIndex(draggablePosition.y);
    const prospectiveEvent = { label: 'New Event', index: index };
    return [...fixedEvents, prospectiveEvent];
  };

  return (
    <div className="App" style={{ display: 'flex', flexDirection: 'row', height: '100vh' }}>
      <div style={{ flex: 1, display: 'flex', flexDirection: 'column' }}>
        <header style={{ textAlign: 'center', padding: '20px', backgroundColor: '#282c34', color: 'white' }}>
          <h1>Timeline of Events</h1>
        </header>
        {/* Set timeline area to relative for absolute event positioning */}
        <main style={{ position: 'relative', height: '100%', overflow: 'hidden' }}>
          {fixedEvents.map((event, index) => (
            <div key={index} style={{
                position: 'absolute',
                top: `calc(50% + ${event.y}px)`,
                left: 'calc(50% - 100px)',
                width: '200px',
                padding: '10px',
                margin: '10px 0',
                backgroundColor: '#f0f0f0',
                border: '1px solid #ccc',
                borderRadius: '5px',
                textAlign: 'center'
              }}>
              {event.label}
            </div>
          ))}
        </main>
      </div>
      <div style={{ width: '200px', padding: '20px', backgroundColor: '#f9f9f9', borderLeft: '1px solid #ccc' }}>
        <h2 style={{ textAlign: 'center' }}>Draggable Event</h2>
        <Draggable
          nodeRef={draggableRef}
          position={draggablePosition}
          onStop={handleStop}
          //onDrag={(e, data) => handleDrag({ x: data.x, y: data.y })}
        >
          <div ref={draggableRef} style={{
              width: '150px',
              padding: '10px',
              backgroundColor: '#d1e7dd',
              border: '1px solid #0f5132',
              borderRadius: '5px',
              textAlign: 'center',
              cursor: 'move'
            }}>
            Drag Me
          </div>
        </Draggable>
      </div>
    </div>
  );
}

export default App;
